#################################################                                              ##         Définition du Home Directory         ##           comme dossier personnel            ##                                              #################################################$Users = (Get-ADUser -Filter *)$Count = 1Foreach ($User in $Users){    Write-Progress -Activity "Ajout du dossier personnel dans le profil" -Status "%effectué" -PercentComplete ($Count/$Users.Length*100)    $samuser = $User.samaccountname    $HomeDir = "\\srvprod\pharmgreen\00_Commun\002_Dossiers Individuels\$samuser"    Set-ADUser $samuser -HomeDirectory $HomeDir -HomeDrive i;    $Count++    sleep -Milliseconds 100}Get-EventLog -list